define("3rdlibs/angular/docs/examples/example-guide-concepts-2/invoice1",["dojo","dijit","dojox"],function(t,i,n){!function(t){"use strict";t.module("invoice1",[]).controller("InvoiceController",function(){this.qty=1,this.cost=2,this.inCurr="EUR",this.currencies=["USD","EUR","CNY"],this.usdToForeignRates={USD:1,EUR:.74,CNY:6.09},this.total=function(t){return this.convertCurrency(this.qty*this.cost,this.inCurr,t)},this.convertCurrency=function(t,i,n){return t*this.usdToForeignRates[n]/this.usdToForeignRates[i]},this.pay=function(){window.alert("Thanks!")}})}(window.angular)});